import React from 'react';
import IconButton from '@material-ui/core/IconButton';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';
import MoreVertIcon from '@material-ui/icons/MoreVert';
import { task } from '../types/task';

export interface ActionsMenuProps {
  promotion: promotion;
  onDelete: (item: promotion) => void;
  onDuplicate: (item: promotion) => void;
  onEdit: (item: promotion) => void;
}

export const ActionsMenu = (props: ActionsMenuProps) => {
  const [anchorEl, setAnchorEl] = React.useState<null | HTMLElement>(null);
  const open = Boolean(anchorEl);
  const { promotion } = props;

  const openMenu = (event: React.MouseEvent<HTMLElement>) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const deleteItem = () => {
    props.onDelete(promotion);
    handleClose();
  }

  const duplicateItem = () => {
    props.onDuplicate(props.promotion);
    handleClose();
  }

  const editItem = () => {
    props.onEdit(props.promotion);
    handleClose();
  }

  return (
    <div>
      <IconButton
        aria-label="more"
        aria-controls="long-menu"
        aria-haspopup="true"
        onClick={openMenu}>
        <MoreVertIcon />
      </IconButton>
      <Menu
        id="menu"
        anchorEl={anchorEl}
        keepMounted
        open={open}
        onClose={handleClose}
        PaperProps={{
          style: {
            width: '20ch',
          },
        }}
      >
        <MenuItem key="edit" onClick={editItem}>
          Edit
        </MenuItem>
        <MenuItem key="duplicate" onClick={duplicateItem}>
          Duplicate
        </MenuItem>
        <MenuItem key="delete" onClick={deleteItem}>
          Delete
        </MenuItem>
      </Menu>
    </div>
  );
}
