import axios, { AxiosResponse } from 'axios';
import { promotion as task } from '../types/task';

const url = process.env.REACT_APP_API_URL as string;

const createPromotions = () => {
    return axios.post(`${url}/batch`);
}

const getPromotions = () => {
    return axios.get<task[]>(`${url}`)
}

const duplicatePromotion = (promotion: task) => {
    return axios.post<task>(url, {
        promotion
    })
}

const deletePromotions = async (ids: string[]) => {
    axios.delete<any>(url, { data: ids })
        .then((response: AxiosResponse) => {
        }).catch((err) => {
            console.log(err)
        })
}

const updatePromotion = async (promotion: task) => {
    axios.post<task[]>(url, {
        promotion
    }).then((response: AxiosResponse) => {
    }).catch((err) => {
        console.log(err)
    });
}

export {
    createPromotions,
    getPromotions,
    duplicatePromotion,
    deletePromotions,
    updatePromotion
};
